import React from 'react';
import Card from "./Card";
import cover from "../assets/img/cover.png";
import { Images } from "./ImageProvider";
import { shuffleCard } from "./CardUtil";

import './CardGrid.css';

const CardGrid = () => {

    const ImageArray1 = Images.map(image => image);
    const ImageArray2 = Images.map(image => image);

    const shuffledCardImage1 = shuffleCard(ImageArray1);
    const shuffledCardImage2 = shuffleCard(ImageArray2);

    // const handleClick = () => {
    //
    // }

    return (
        <div className="card-grid">
            <div className="row">
                {shuffledCardImage1.map((image, index) => (
                    <Card key={index}  backImage={cover} frontImage={image} />
                ))}
                {shuffledCardImage2.map((image, index) => (
                    <Card key={index}  backImage={cover} frontImage={image} />
                ))}
            </div>
        </div>
    );
};

export default CardGrid;
