import React, {useEffect, useState} from 'react';

import './Card.css';

const Card = ({frontImage, backImage}) => {
    const [image, setImage] = useState('');

    useEffect(() => {
        setImage(backImage);
    }, []);

    const handleClick = () => {
        if ( image === frontImage) {
            setImage(backImage);
            return;
        }

        setImage(frontImage);
    };

    return (
        <div className="card">
            <img src={image} onClick={handleClick} />
        </div>
    );
};

export default Card;
