import React, {useEffect, useState} from 'react';

import './Card.css';
import cover from "../assets/img/cover.png";
import sword from "../assets/img/sword-1.png";

const Card = ({index, handleCardClick, flipCard, frontImage, backImage}) => {
    const [className, setClassName] = useState('flip-card-inner');

    useEffect(() => {
        setClassName('flip-card-inner flip-back');
    }, [flipCard, backImage]);

    const handleClick = () => {
        setClassName('flip-card-inner flip-front');
        handleCardClick(index);
    };

    return (
        <div className="flip-card">
            <div className={className}>
                <div>
                    <img className="flip-card-front" onClick={handleClick} src={backImage} alt="Avatar" />
                </div>
                <div>
                    <img className="flip-card-back" src={frontImage} alt="Avatar" />
                </div>
            </div>
        </div>
    );
};

export default Card;
