import React, {useEffect, useState} from 'react';

import './Card.css';

const Card = ({index, checkSelectedCard, reference, flipCard, frontImage, backImage}) => {
    const [image, setImage] = useState('');

    useEffect(() => {
        setImage(backImage);
    }, [flipCard]);

    const handleClick = () => {
        // if ( image === frontImage) {
        //     setImage(backImage);
        //     return;
        // }

        setImage(frontImage);
        checkSelectedCard(index);
    };

    return (
        <div ref={reference} className="card">
            <img src={image} onClick={handleClick} />
        </div>
    );
};

export default Card;
