import React, {useEffect, useState} from 'react';

import './Card.css';

const Card = ({index, handleCardClick, flipCard, frontImage, backImage}) => {
    const [image, setImage] = useState('');

    useEffect(() => {
        setImage(backImage);
        console.log(`Card of Index: ${index} was flipped`);
    }, [flipCard]);

    const handleClick = () => {
        setImage(frontImage);
        handleCardClick(index);
    };

    return (
        <div className="card">
            <img src={image} onClick={handleClick} />
        </div>
    );
};

export default Card;
