import React, {useEffect, useState} from 'react';
import CardGrid from "./components/CardGrid";

import './App.css';
import {shuffleCard} from "./components/CardUtil";
import {Images} from "./components/ImageProvider";

const App = () => {

    const [shuffledCardImage, setShuffledCardImage] = useState(shuffleCard([...Images, ...Images]));

    const handleClick = () => {
        const newShuffledCardImage = shuffleCard([...Images, ...Images]);
        setShuffledCardImage([...newShuffledCardImage]);
    };

  return (
    <div className="App">
        <h2>Magic Match</h2>
        <button onClick={handleClick}>New Game</button>
        <CardGrid shuffledCardImage={shuffledCardImage} />
    </div>
  );
}

export default App;
