import React, {useState} from 'react';
import sword from "../assets/img/sword-1.png";
import ring from "../assets/img/ring-1.png";

import './TestGround.css';

const FlippingCard = () => {

    const [className, setClassName] = useState('flip-card-inner');

    const handleClickFront = () => {
        setClassName('flip-card-inner flip-front');
    }

    const handleClickBack = () => {
        setClassName('flip-card-back flip-back');
    }

    return (
        <div>
            <h1>Card Flip with Text</h1>
            <h3>Hover over the image below:</h3>

            <div className="flip-card">
                <div className={className}>
                    <div className="flip-card-front" onClick={handleClickFront}>
                        <img src={sword} alt="Avatar" />
                    </div>
                    <div className="flip-card-back" onClick={handleClickBack}>
                        <img src={ring} alt="Avatar" />
                    </div>
                </div>
            </div>
        </div>
    );
}

export default FlippingCard;
