import React, {useRef, useState} from 'react';
import Card from "./Card";
import cover from "../assets/img/cover.png";

import './CardGrid.css';
import {shuffleCard} from "./CardUtil";
import {Images} from "./ImageProvider";

const CardGrid = () => {
    const cardRefs = useRef(new Array());

    const shuffledCards = shuffleCard([...Images, ...Images]);
    const [flipCard, setFlipCard] = useState(false);

    const [firstCardSelected, setFirstCardSelected] = useState(null);

    const checkSelectedCard = (clickedCardIndex) => {
        if (firstCardSelected) {
            if (firstCardSelected !== clickedCardIndex) {
                turnCardsOver(firstCardSelected, clickedCardIndex);
                return;
            }

            setFirstCardSelected(null);
            return;
        }

        setFirstCardSelected(clickedCardIndex);
    }

    const turnCardsOver = (firstCardSelected, clickedCardIndex) => {

    }

    return (
        <div className="card-grid">
            <div className="row">
                {shuffledCards.map((image, index) => (
                    <Card
                        key={index}
                        index={index}
                        checkSelectedCard={checkSelectedCard}
                        flipCard={flipCard}
                        ref={(card) => cardRefs.current.push(card)}
                        backImage={cover}
                        frontImage={image} />
                ))}
            </div>
        </div>
    );
};

export default CardGrid;
