import React, {createRef, useEffect, useState} from 'react';
import Card from "./Card";
import cover from "../assets/img/cover.png";

import './CardGrid.css';
import {shuffleCard} from "./CardUtil";
import {Images} from "./ImageProvider";

const CardGrid = () => {

    const shuffledCards = shuffleCard([...Images, ...Images]);
    const noOfShuffledCards = shuffledCards.length;

    const [flipCard, setFlipCard] = useState(false);
    const [firstCardSelectedIndex, setFirstCardSelectedIndex] = useState(null);
    const [cardRefs, setCardRefs] = useState([]);

    useEffect(() => {
        setCardRefs(
            (cardRefs) => Array(noOfShuffledCards)
                .fill()
                .map((_, i) => cardRefs[i] || createRef())
        );
    }, [noOfShuffledCards]);


    const checkSelectedCard = (clickedCardIndex) => {
        if (firstCardSelectedIndex) {
            const firstSelectedCard = cardRefs.current[firstCardSelectedIndex];
            const selectCard = cardRefs.current[firstCardSelectedIndex];

            console.log(firstSelectedCard);
            console.log(selectCard);

            // if (firstCardSelectedIndex !== clickedCardIndex) {
            //     turnCardsOver(firstCardSelectedIndex, clickedCardIndex);
            //     return;
            // }

            setFirstCardSelectedIndex(null);
            return;
        }

        setFirstCardSelectedIndex(clickedCardIndex);
    }

    const turnCardsOver = (firstCardSelected, clickedCardIndex) => {

    }

    return (
        <div className="card-grid">
            <div className="row">
                {shuffledCards.map((image, index) => (
                    <Card
                        key={index}
                        index={index}
                        checkSelectedCard={checkSelectedCard}
                        flipCard={flipCard}
                        ref={cardRefs[index]}
                        backImage={cover}
                        frontImage={image} />
                ))}
            </div>
        </div>
    );
};

export default CardGrid;
