import React, {createRef, useEffect, useRef, useState} from 'react';
import Card from "./Card";
import cover from "../assets/img/cover.png";

import './CardGrid.css';
import {shuffleCard} from "./CardUtil";
import {Images} from "./ImageProvider";

const shuffledCards = shuffleCard([...Images, ...Images]);
console.log('shuffledCards: ', shuffledCards);


const CardGrid = () => {

    const cardLength = shuffledCards.length;
    const [flipCards, setFlipCards] = useState([]);
    const [firstCardSelectedIndex, setFirstCardSelectedIndex] = useState(null);
    const rightGuess = useRef([]);
    const reRendered = useRef(0);

    useEffect(() => {
        setFlipCards(
            Array(cardLength).fill()
        );
    }, []);

    const handleCardClick = (clickedCardIndex) => {
        console.log('Click function Called');
        if (firstCardSelectedIndex != null) {

            if(shuffledCards[firstCardSelectedIndex] === shuffledCards[clickedCardIndex] &&
                firstCardSelectedIndex !== clickedCardIndex)
            {
                rightGuess.current.push(firstCardSelectedIndex);
                rightGuess.current.push(clickedCardIndex);
                console.log('Clicked Cards have the same image');
            }

            //setTurnCardsOver;
            console.log('resetting firstCardSelectedIndex');
            setFirstCardSelectedIndex(null);
            return;
        }

        setFirstCardSelectedIndex(clickedCardIndex);
    }

    // const turnCardsOver = () => {
    //
    // }

    console.log('rightGuess.current: ', rightGuess.current);
    //console.log('firstCardSelectedIndex ', firstCardSelectedIndex);

    return (
        <div className="card-grid">
            <div className="row">
                {shuffledCards.map((image, index) => (
                    <Card
                        key={index}
                        index={index}
                        handleCardClick={handleCardClick}
                        flipCard={flipCards[index]}
                        backImage={cover}
                        frontImage={image} />
                ))}
            </div>
            <p>Re-rendered: {reRendered + 1}</p>
        </div>
    );
};

export default CardGrid;
